const userinfo = () => {
  const config = {
    headers: {
      Authorization: "Token " + localStorage.getItem("token"),
    },
  };
  axios
    .get("api/auth/user/", config)
    .then((res) => {
      this.setState({ username: res.data.username });
    })
    .catch((err) => {
      this.setState({ username: "" });
      localStorage.clear();
    });
};

export default userinfo;
