import React, { Component } from "react";
import axios from "axios";
import NasaNav from "./Nav";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      username: "",
      password: "",
    };
  }

  // Add componentDidMount()
  componentDidMount() {}
  // Main variable to render items on the screen
  // Submit an item
  handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("auth/register/", this.state)
      .then((respose) => {
        console.log(respose);
        document.getElementById("verification").innerText="";
      })
      .catch((error) => {
        console.log(error);
      });
  };
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  resen

  // Create item
  // -I- Start by visual effects to viewer
  render() {
    const { username, email, password } = this.state;
    return (
      <div>
        {localStorage.getItem("token") ? (
          (window.location = "/")
        ) : (
          <React.Fragment>
            <NasaNav register="active" />
            <div className="container">
              <br />
              <br />
              <br />
              <br />
              <br />
              <h3>Register </h3>
              <p>
                Please enter the following details as explained to finish your
                registration.
              </p>
              <p id="verification" style={{ color: "red" }}>We have sent you a verification link ! Click <a onClick={this.resend}> Resend </a>to resend it</p>
              <p id="error" style={{ color: "red" }}></p>
              <form>
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    name="email"
                    value={email}
                    placeholder="Email address"
                    onChange={this.changeHandler}
                  />
                </div>
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    name="username"
                    value={username}
                    placeholder="Username"
                    onChange={this.changeHandler}
                  />
                </div>
                <div className="input-group">
                  <input
                    type="password"
                    className="form-control"
                    name="password"
                    value={password}
                    placeholder="Password"
                    onChange={this.changeHandler}
                  />
                </div>
                <button onClick={this.handleSubmit} className="btn btn-primary">
                  Submit
                </button>
              </form>
            </div>
          </React.Fragment>
        )}
      </div>
    );
  }
}
export default App;
