import React, { Component } from "react";
import axios from "axios";
import NasaNav from "./Nav";

class login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
    };
  }

  displayResend = () => {
    document.getElementById("resend").innerHTML = "";
    document.getElementById(
      "error"
    ).innerText = `We have sent you a verification link to activate your account, please verify otherwise you won't be able to log in ! 
          If you did not receive it, please verify the e-mail you provided or check your spam !`;
    document.getElementById("wait").innerText =
      "Wait 60 seconds to be able to resend the verification email again";
    setTimeout(() => {
      document.getElementById(
        "resend"
      ).innerText = `Click here to resend the e-mail.`;
      document.getElementById("wait").innerText = "";
    }, 60000);
    setTimeout(() => {
      document.getElementById("error").innerText = ``;
    }, 30000);
  };

  Resend = (e) => {
    e.preventDefault();
    axios.post("auth/request-resend-email/", this.state).then((response) => {});
  };

  handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("auth/login/", this.state)
      .then((res) => {
        localStorage.setItem("access", res.data.tokens.access);
        localStorage.setItem("refresh", res.data.tokens.refresh);
        window.location = "/";
      })
      .catch((error) => {
        console.log(error.response);
        if (error.response.data.detail) {
          if (error.response.data.detail === "Email is not verified") {
            document.getElementById("or").innerText = "or ";
            document.getElementById(
              "resend"
            ).innerHTML = `<a href="#" onClick=${this.resend} id="resend">Resend Verification E-mail ?</a>`;
            document.getElementById("error").innerText =
              error.response.data.detail +
              "! Click Resend Verification E-mail to resend the email";
            document.getElementById("EmailSpan").innerText = "";
          } else {
            document.getElementById("error").innerText =
              error.response.data.detail;
            document.getElementById("EmailSpan").innerText = "";
          }
        } else if (error.response.data.email) {
          document.getElementById("EmailSpan").innerText =
            error.response.data.email;
        } else {
          document.getElementById("error").innerText =
            "Wrong E-mail or Password! ";
        }
      });
  };
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { email, password } = this.state;
    return (
      <div>
        {localStorage.getItem("access") ? (
          (window.location = "/")
        ) : (
          <React.Fragment>
            <NasaNav login="active" />
            <div className="container">
              <br />
              <br />
              <br />
              <br />
              <br />
              <h3>Log in</h3>
              <p>Please enter the following details as explained to login.</p>
              <p id="error" style={{ color: "red" }}></p>
              <form>
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    name="email"
                    value={email}
                    placeholder="email"
                    onChange={this.changeHandler}
                  />
                  <span
                    id="EmailSpan"
                    className="input-group-addon"
                    style={{ color: "red" }}
                  ></span>
                </div>
                <div className="input-group">
                  <input
                    type="password"
                    className="form-control"
                    name="password"
                    value={password}
                    placeholder="Password"
                    onChange={this.changeHandler}
                  />
                  <span
                    id="PassworSpan"
                    className="input-group-addon"
                    style={{ color: "red" }}
                  ></span>
                </div>
                <div>
                  <a href="forgot">Forgot Password ? </a>
                  <span id="or"></span>
                  <div id="resend">
                    <p id="wait"></p>
                  </div>
                </div>
                <button onClick={this.handleSubmit} className="btn btn-primary">
                  Submit
                </button>
              </form>
            </div>
          </React.Fragment>
        )}
      </div>
    );
  }
}
export default login;
