import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import CreatorInfo from "./components/CreatorInfo";
import HomePage from "./components/HomePage";
import WrongPage from "./components/WrongPage";
import Comments from "./components/Comments";
import "bootstrap/dist/css/bootstrap.min.css";
import login from "./components/login";
import register from "./components/register";
import React, { Component } from "react";
import axios from "axios";
import NasaNav from "./components/Nav";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        email: "",
        password: "",
    };
  }


  handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("auth/login/", this.state)
      .then((res) => {
        localStorage.setItem("access-token", res.data.tokens.access);
        localStorage.setItem("refresh-token", res.data.tokens.refresh);
        window.location='/'
      })
      .catch((error) => {
        document.getElementById("error").innerText =
          "Wrong email/password (email,password are required )";
      });
  };
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { email, password } = this.state;
    return (
      <div>
        <Router>
          <div className="App">
            <Switch>
              <Route path="/" exact component={HomePage} />
              <Route path="/creatorinfo" component={CreatorInfo} />
              <Route path="/comments" component={Comments} />
              <Route path="/login">
                <div>
                  {localStorage.getItem("token") ? (
                    (window.location = "/")
                  ) : (
                    <React.Fragment>
                      <NasaNav login="active" username={this.state.username} />
                      <div className="container">
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <h3>Log in</h3>
                        <p>
                          Please enter the following details as explained to
                          login.
                        </p>
                        <p id="error" style={{ color: "red" }}></p>
                        <form>
                          <div className="input-group">
                            <input
                              type="text"
                              className="form-control"
                              name="email"
                              value={email}
                              placeholder="email"
                              onChange={this.changeHandler}
                            />
                          </div>
                          <div className="input-group">
                            <input
                              type="password"
                              className="form-control"
                              name="password"
                              value={password}
                              placeholder="Password"
                              onChange={this.changeHandler}
                            />
                          </div>
                          <button
                            onClick={this.handleSubmit}
                            className="btn btn-primary"
                          >
                            Submit
                          </button>
                        </form>
                      </div>
                    </React.Fragment>
                  )}
                </div>
              </Route>
              <Route path="/register" component={register} />
              <Route path="/" component={WrongPage} />
            </Switch>
          </div>
        </Router>
      </div>
    );
  }
}

export default App;
