import React, { Component } from "react";
import NasaNav from "./Nav";
import axios from "axios";

export class Change extends Component {
  constructor(props) {
    super(props);

    this.state = {
      password: "",
      token: localStorage.getItem("Token-reset"),
    };
  }

  componentDidMount() {
    const url = window.location.href;
    const array = url.split("uidb=");
    array.shift();
    const StringToken = array.toString();
    localStorage.setItem("Token-reset", StringToken);
  }

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    if (
      document.getElementById("password1").value ===
      document.getElementById("password2").value
    ) {
      axios
        .patch("auth/password-reset-complete", this.state)
        .then((res) => {
          console.log(res);
          localStorage.clear();
          window.location = "/login";
        })
        .catch((err) => console.log(err));
    }
  };
  render() {
    return (
      <div>
        <NasaNav />
        <React.Fragment>
          <NasaNav login="active" />
          <div className="container">
            <br />
            <br />
            <br />
            <br />
            <br />
            <h3>Change your password</h3>
            <p id="error" style={{ color: "red" }}></p>
            <form>
              <div className="input-group">
                <input
                  id="password1"
                  type="password"
                  className="form-control"
                  name="password"
                  value={this.state.password}
                  placeholder="Enter Your Password !"
                  onChange={this.changeHandler}
                />
              </div>
              <div className="input-group">
                <input
                  id="password2"
                  type="password"
                  className="form-control"
                  name="password"
                  placeholder="Confirm Your Password !"
                />
              </div>
              <button onClick={this.handleSubmit} className="btn btn-primary">
                Submit
              </button>
            </form>
          </div>
        </React.Fragment>
      </div>
    );
  }
}

export default Change;
