import React, { Component } from "react";
// importing all of these classes from reactstrap module
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Input,
  Label,
} from "reactstrap";

// build a class base component
class CustomModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeItemComment: this.props.activeItemComment,
    };
  }
  // changes handler to check if a checkbox is checed or not
  handleChange = (e) => {
    let { name, value } = e.target;
    const activeItemComment = {
      ...this.state.activeItemComment,
      [name]: value,
    };
    this.setState({ activeItem });
  };

  // rendering modal in the custommodal class received toggle and on save as props,
  render() {
    const { toggle, onSave } = this.props;
    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}> Comment settings </ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="description">Comment</Label>
              <Input
                type="text"
                name="message"
                value={this.state.activeItem.message}
                onChange={this.handleChange}
                placeholder="Enter The Comment Message"
              />
            </FormGroup>
          </Form>
        </ModalBody>
        {/* create a modal footer */}
        <ModalFooter>
          <Button color="success" onClick={() => onSave(this.state.activeItem)}>
            Save
          </Button>
        </ModalFooter>
      </Modal>
    );
  }
}
export default CustomModal;
