import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import CreatorInfo from "./components/CreatorInfo";
import HomePage from "./components/HomePage";
import WrongPage from "./components/WrongPage";
import Comments from "./components/Comments";
import "bootstrap/dist/css/bootstrap.min.css";
import React, { Component } from "react";
import Forgot from "./components/Forgot";
import Change from "./components/Change";
import ChangePass from "./components/change-password";
import login from "./components/login";
import register from "./components/register";
import WebSocketInstance from "./websocket";
import "./assets/style.css";
import * as navActions from "./redux/chat/actions/nav";
import * as messageActions from "./redux/chat/actions/message";
import { connect } from "react-redux";
import BaseRouter from "./routes";
import Sidepanel from "./containers/Sidepanel";
import Profile from "./containers/Profile";
import AddChatModal from "./containers/Popup";


class App extends Component {

  constructor(props) {
    super(props);
    WebSocketInstance.addCallbacks(
      this.props.setMessages.bind(this),
      this.props.addMessage.bind(this)
    );
  }



  render() {
    return (
        <Router>
          <Switch>
            <Route path="/" exact component={HomePage} />
            <Route path="/creatorinfo" component={CreatorInfo} />
            <Route path="/comments" component={Comments} />
            <Route path="/login" component={login} />
            <Route path="/forgot" component={Forgot} />
            <Route path="/change" component={Change} />
            <Route path="/settings/password" component={ChangePass} />
            <Route path="/register" component={register} />
            <Route path="/" component={WrongPage} />
            <div id="frame">
              <Sidepanel />
              <div className="content">
                <AddChatModal
                  isVisible={this.props.showAddChatPopup}
                  close={() => this.props.closeAddChatPopup()}
                />
                <Profile />
                <BaseRouter />
              </div>
            </div>
          </Switch>
        </Router>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    showAddChatPopup: state.nav.showAddChatPopup,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    closeAddChatPopup: () => dispatch(navActions.closeAddChatPopup()),
    addMessage: (message) => dispatch(messageActions.addMessage(message)),
    setMessages: (messages) => dispatch(messageActions.setMessages(messages)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
