import React, { Component } from "react";
import NasaNav from "./Nav";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Input,
  Label,
} from "reactstrap";

class Forgot extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
    };
  }

  handleChange = (e) => {
    let { name, value } = e.target;
    const activeItem = { ...this.state.activeItem, [name]: value };
    this.setState({ activeItem });
  };

  render() {
    return (
      <div>
        <NasaNav />
        <br />
        <br />
        <br />
        <br />
        <Modal>
          <ModalHeader> Comment settings </ModalHeader>
          <ModalBody>
            <Form>
              <FormGroup>
                <Label for="description">Comment</Label>
                <Input
                  type="text"
                  name="message"
                  value={this.state.activeItem.message}
                  onChange={this.handleChange}
                  placeholder="Enter The Comment Message"
                />
              </FormGroup>
            </Form>
          </ModalBody>
          {/* create a modal footer */}
          <ModalFooter>
            <Button
              color="success"
              onClick={this}
            >
              Send
            </Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default Forgot;
