import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import CreatorInfo from "./components/CreatorInfo";
import HomePage from "./components/HomePage";
import WrongPage from "./components/WrongPage";
import Comments from "./components/Comments";
import "bootstrap/dist/css/bootstrap.min.css";
import login from "./components/login";
import register from "./components/register";
import React, { Component } from "react";
import axios from "axios";
import NasaNav from "../compon/Nav";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      info: {
        email: "",
        password: "",
      },
      username: "",
    };
  }

  handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("auth/login/", this.state)
      .then((res) => {
        localStorage.setItem("access-token", res.data.tokens.access);
        localStorage.setItem("refresh-token", res.data.tokens.refresh);
      })
      .catch((error) => {
        document.getElementById("error").innerText =
          "Wrong email/password (email,password are required )";
      });
  };
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    return (
      <div>
        <Router>
          <div className="App">
            <Switch>
              <Route path="/" exact component={HomePage} />
              <Route path="/creatorinfo" component={CreatorInfo} />
              <Route path="/comments" component={Comments} />
              <Route path="/login" component={login} />
              <Route path="/register" component={register} />
              <Route path="/" component={WrongPage} />
            </Switch>
          </div>
        </Router>
      </div>
    );
  }
}

export default App;
