import React, { Component } from "react";
import Modal from "./Modal";
import axios from "axios";
import NasaNav from "./Nav";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      activeItem: {
        created_by: "",
        message: "",
        created_dt: "",
      },
      taskList: [],
    };
  }

  // Add componentDidMount()
  componentDidMount() {
    document.title = 'Mohamed FM |'
    this.refreshList();
    const config = {
      headers: {
        Authorization: "Token " + localStorage.getItem("token"),
      },
    };
    axios
      .get("api/auth/user/", config)
      .then((res) => {
        this.setState({ created_by: res.data.username });
      })
      .catch((err) => {
        this.setState({ created_by: "" });
        localStorage.clear();
      });
  }

  refreshList = () => {
    axios //Axios to send and receive HTTP requests
      .get("http://localhost:8000/api/tasks/")
      .then((res) => this.setState({ taskList: res.data }))
      .catch((err) => console.log(err));
  };
  // Main variable to render items on the screen
  renderItems = () => {
    return this.state.taskList.map((item) => (
      <li
        key={item.id}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span className={`todo-title mr-2 `}>
          <h4 id={item.id}>
            Created by : {item.created_by}
            <span style={{ fontSize: "12px" }}> {item.created_dt}</span>{" "}
          </h4>{" "}
          {item.message}
        </span>
        <span>
          {item.created_by === this.state.created_by ? (
            <div>
              <button
                onClick={() => this.editItem(item)}
                className="btn btn-secondary mr-2"
              >
                Edit
              </button>
              <button
                onClick={() => this.handleDelete(item)}
                className="btn btn-danger"
              >
                Delete
              </button>
            </div>
          ) : null}
        </span>
      </li>
    ));
  };
  ////add this after modal creation
  toggle = () => {
    //add this after modal creation
    this.setState({ modal: !this.state.modal }); //add this after modal creation
  };
  // Submit an item
  handleSubmit = (item) => {
    this.toggle();
    if (item.id) {
      // if old post to edit and submit
      axios
        .put(`http://localhost:8000/api/tasks/${item.id}/`, item)
        .then((res) => this.refreshList());
      return;
    }
    // if new post to submit
    axios
      .post("http://localhost:8000/api/tasks/", item)
      .then((res) => this.refreshList());
  };

  // Delete item
  handleDelete = (item) => {
    if (this.state.created_by) {
      axios
        .delete(`http://localhost:8000/api/tasks/${item.id}/`)
        .then((res) => this.refreshList());
    } else {
      window.location = "/login";
    }
  };

  // Create item
  createItem = () => {
    if (localStorage.getItem("token")) {
      const item = {
        created_by: this.state.created_by,
        message: "",
        created_dt: "",
      };
      this.setState({ activeItem: item, modal: !this.state.modal });
    } else {
      window.location = "/login";
    }
  };

  //Edit item
  editItem = (item) => {
    if (this.state.created_by) {
      this.setState({ activeItem: item, modal: !this.state.modal });
    } else {
      window.location = "/login";
    }
  };

  // -I- Start by visual effects to viewer
  render() {
    return (
      <div>
        <br />
        <br />
        <main className="content">
          <h1 className="text-black text-uppercase text-center my-4">
            Comments
          </h1>
          <div className="row ">
            <div className="card p-3">
              <div className="">
                <button onClick={this.createItem} className="btn btn-primary">
                  Add Comment
                </button>
              </div>
              <ul className="list-group list-group-flush">
                {this.renderItems()}
              </ul>
            </div>
          </div>
          {this.state.modal ? (
            <Modal
              activeItem={this.state.activeItem}
              toggle={this.toggle}
              onSave={this.handleSubmit}
            />
          ) : null}
        </main>
      </div>
    );
  }
}
export default App;
