import React, { Component } from "react";
import axios from "axios";
import NasaNav from "./Nav";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
    };
  }

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  Resend = (e) => {
    e.preventDefault();
    axios
      .post("auth/request-resend-email/", this.state)
      .then((response) => {
        document.getElementById(
          "verification"
        ).innerText = `We have sent you a verification link to activate your account, please verify otherwise you won't be able to log in ! 
          If you did not receive it, please verify the e-mail you provided or check your spam !`;
      })
      .catch((error) => {
        console.log(error.response.data);
        this.setState({ error: error.response.data });
      });
  };

  // Create item
  // -I- Start by visual effects to viewer
  render() {
    const {email} = this.state;
    return (
      <div>
        {localStorage.getItem("access") ? (
          (window.location = "/")
        ) : (
          <React.Fragment>
            <NasaNav register="active" />
            <div className="container">
              <br />
              <br />
              <br />
              <br />
              <br />
              <h3>Resend E-mail Verification </h3>
              <p>
                Please enter the following details as explained to resend your
                e-mail.
              </p>
              <p id="verification" style={{ color: "red" }}></p>
              <form>
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    name="email"
                    value={email}
                    placeholder="Email address"
                    onChange={this.changeHandler}
                  />
                  <span className="input-group-addon" style={{ color: "red" }}>
                    {this.state.error ? this.state.error.errors.email : null}
                  </span>
                </div>
                <button onClick={this.Resend} className="btn btn-primary">
                  Submit
                </button>
                <a href="#" onClick={this.Resend}>
                  Click here to resend the e-mail.
                </a>
              </form>
            </div>
          </React.Fragment>
        )}
      </div>
    );
  }
}
export default App;
